Write them


src/lib.rs

fn heron(a:f32, b:f32, c:f32) -&gt; f32 {
    let s = (a + b + c)/2.0_f32;
    return (s * (s - a) * (s - b) * (s - c)).sqrt();
}

fn kahan(a:f32, b:f32, c:f32) -&gt; f32 {
    let mut s = vec![a,b,c];
    s.sort_by(|a, b| a.partial_cmp(b).unwrap()); // "partial"? Why not `.sort`?
    let mut prod = s[0] + s[1] + s[2];
    prod *= s[2] - (s[0] - s[1]);
    prod *= s[2] + (s[0] - s[1]);
    prod *= s[0] + (s[1] - s[2]);
    return prod.sqrt() / 4.0;
}

